mapper.select:
		select * from
		(
 			select a.*,rownum rnum from 
 			(
  				select * from community where cNum=#{cNum}
  				
  					and ${field} like '%'||#{keyword}||'%'
  				
  				order by pNum desc
  			) a
  		)
		 where rnum>=#{startRow} and rnum<=#{endRow}

select hit
from community
where pNum = #{pNum}

select nohit
from community
where pNum = #{pNum}
