mapper.select:
select type
from matchinfo
where matchid = #{matchid}

select * from( select rownum rn, t.* from ( SELECT tm.*, c.name, c.picture, m.type, m.runtime, m.starttime, winlose FROM teammemberinfo tm, teaminfo t, matchinfo m, champion c WHERE tm.matchid = m.matchid
and m.type = #{type}
AND tm.championid = c.championid and tm.matchid = t.matchid and tm.teamid = t.teamid AND tm.snickname = #{snickname} order by tm.matchid desc )t ) where rn<=20
